/*
 * @Author: Lucas
 * @Date: 2021-01-28 15:57:03
 * @LastEditTime: 2021-01-28 16:24:25
 * @LastEditors: Please set LastEditors
 * @Description: 参考 飞冰 （https://ice.work/docs/icestark/guide/interaction）应用间通信
 * @FilePath: /qiankun-react/master/src/store/index.js
 */

import { store } from '@ice/stark-data';

/**
 * @description: 设置/初始化 store 中的变量
 * @param {*} key
 * @param {*} value
 */
function set(key, value) {
  store.set(key, value);
}

/**
 * @description: 从 store 中获取变量
 * @param {*} key
 * @return {*} 返回对应 key 的值
 */
function get(key) {
  return store.set(key)
}

export default store;
